import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import Header from './components/Header';
import SearchBar from '../components/SearchBar';
import SongList from '../components/SongList';
import MusicPlayer from '../components/MusicPlayer';
import Playlist from '../components/Playlist';
import Footer from '../components/Footer';
import '../styles.css';

const Home = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [songs, setSongs] = useState([]);
  const [currentSong, setCurrentSong] = useState(null);
  const [playlists, setPlaylists] = useState([]);

  const fetchInitialSongs = async () => {
    try {
      const response = await axios.get('https://thingproxy.freeboard.io/fetch/https://api.deezer.com/chart/0/tracks?limit=20');
      const fetchedSongs = response.data.data.map((song) => ({
        id: song.id,
        title: song.title,
        artist: song.artist.name,
        duration: song.duration,
        preview: song.preview,
        image: song.album.cover,
      }));
      setSongs(fetchedSongs);
    } catch (error) {
      console.error('Error fetching initial songs:', error);
    }
  };

  const handleSearch = useCallback(async () => {
    if (searchTerm) {
      try {
        const response = await axios.get(`https://thingproxy.freeboard.io/fetch/https://api.deezer.com/search?q=${searchTerm}&limit=20`);
        const fetchedSongs = response.data.data.map((song) => ({
          id: song.id,
          title: song.title,
          artist: song.artist.name,
          duration: song.duration,
          preview: song.preview,
          image: song.album.cover,
        }));
        setSongs(fetchedSongs);
      } catch (error) {
        console.error('Error fetching search results:', error);
      }
    }
  }, [searchTerm]);

  useEffect(() => {
    fetchInitialSongs();
  }, []);

  const playSong = (song) => {
    setCurrentSong(song);
  };

  const nextSong = () => {
    const currentIndex = songs.findIndex((song) => song.id === currentSong.id);
    const nextIndex = (currentIndex + 1) % songs.length;
    setCurrentSong(songs[nextIndex]);
  };

  const prevSong = () => {
    const currentIndex = songs.findIndex((song) => song.id === currentSong.id);
    const prevIndex = (currentIndex - 1 + songs.length) % songs.length;
    setCurrentSong(songs[prevIndex]);
  };

  const addPlaylist = (name) => {
    setPlaylists([...playlists, { name, songs: [] }]);
  };

  const addSongToPlaylist = (playlistName, song) => {
    setPlaylists(playlists.map((playlist) =>
      playlist.name === playlistName ? { ...playlist, songs: [...playlist.songs, song] } : playlist
    ));
  };

  return (
    <div className="app">
      <Header />
      <SearchBar searchTerm={searchTerm} setSearchTerm={setSearchTerm} handleSearch={handleSearch} />
      <div className="main-section">
        <aside className="sidebar">
          <Playlist playlists={playlists} addPlaylist={addPlaylist} addSongToPlaylist={addSongToPlaylist} songs={songs} />
        </aside>
        <div className="content">
          <SongList songs={songs} playSong={playSong} />
          <MusicPlayer currentSong={currentSong} nextSong={nextSong} prevSong={prevSong} />
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Home;
